package br.com.hfsuprimento.model;import java.io.Serializable;import br.com.topsys.util.TSUtil;@SuppressWarnings("serial")public class UnidadeMedidaModel implements Serializable {	private Long id;	private String descricao;	public Long getId() {		return TSUtil.tratarLong(id);	}	public void setId(Long id) {		this.id = id;	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}}